class PostComments{constructor(e){this.postId=e,this.postContainer=$("#post-"+e),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each((function(){t.deleteComment($(this))}))}createComment(e){let t=this;this.newCommentForm.submit((function(o){console.log("post comments"),o.preventDefault();$.ajax({type:"post",url:"/comments/create",data:$(this).serialize(),success:function(o){console.log(o.data);let n=t.newCommentDom(o.data);$("#post-comments-"+e).prepend(n),t.deleteComment($(" .delete-comment-button",n)),new Noty({theme:"relax",text:"Comment published!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}newCommentDom(e){return console.log(e),$(`\n            <li id="comment-${e.id}" class="one-comment">\n                <span>${e.content} \n                    <br>\n                    <small>${e.user}</small>\n                </span>\n\n                <small class=" delete-comment">\n                    <a class="delete-comment-button" href="/comments/destroy/${e.id}">\n                        <span class="material-icons delete-comment">delete</span>\n                    </a>    \n                </small>\n            </li>  \n        `)}deleteComment(e){$(e).click((function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){$("#comment-"+e.data.comment_id).remove(),new Noty({theme:"relax",text:"Comment Deleted",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}}